import React from 'react';
import { View, Text, StyleSheet, TouchableWithoutFeedback} from 'react-native';

import {MaterialCommunityIcons} from '@expo/vector-icons'
import colors from '../config/color';

import AppCard from '../components/AppCard';
import AppText from '../components/AppText';
import AppBubbleText from '../components/AppBubbleText';



function MoreInformationScreen({route}) {

    const item = route.params.item

    return (
        <View style={styles.container}>
            <View style={styles.topBar}>
                <AppText style={styles.text}>Listings</AppText>
            </View>
            <AppCard
                image={item.image}
                title={item.place}
                category={item.category}
                deleteOnPress={() => alert("Yo!")}/>
            <AppBubbleText>{item.description}</AppBubbleText>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        marginTop:55
    },
    text: {
        fontWeight: 'bold',
        marginBottom: 15,
        fontSize: 20,
        paddingLeft: 20
    },
    topBar: {
        flexDirection: "row",
        justifyContent: 'space-between',
        paddingRight: 20
    }
})

export default MoreInformationScreen;
